# Создать базовый шаблон для интернет-магазина, содержащий общие элементы дизайна (шапка, меню, подвал),
# и дочерние шаблоны для страниц категорий товаров и отдельных товаров.
# Например, создать страницы «Одежда», «Обувь» и «Куртка», используя базовый шаблон.
import datetime

from flask import Flask, render_template

shop_app = Flask(__name__)


@shop_app.route('/')
def index():
    return render_template('index.html')


@shop_app.route('/shoes/')
def shoes():
    shoes = [
        {'title': 'боты брутальные', 'date': f"{datetime.datetime.now()}",
         'pic': 'https://img.freepik.com/free-photo/leather-boots_1203-8146.jpg?size=626&ext=jpg&ga=GA1.1.1662083365.1715872630&semt=ais_user_b'},
        {'title': 'чоботы могучие', 'date': f"{datetime.datetime.now()}",
         'pic': 'https://img.freepik.com/premium-photo/resilient-footsteps-celebrating-labor-day-with-worn-work-boots_1000764-533.jpg?size=626&ext=jpg&ga=GA1.1.1662083365.1715872630&semt=ais_user_b'},
        {'title': 'коньки катучие', 'date': f"{datetime.datetime.now()}",
         'pic': 'https://img.freepik.com/free-photo/ice-skates-in-studio-still-life_23-2150558977.jpg?size=626&ext=jpg&uid=R149106118&ga=GA1.1.1662083365.1715872630&semt=ais_user'}
    ]
    return render_template('shoes.html', shoes=shoes)


@shop_app.route('/clothes/')
def clothes():
    clothes = [
        {'title': 'подпивасная', 'date': f"{datetime.datetime.now()}",
         'pic': 'data:image/jpeg;base64,'},
        {'title': 'панковая', 'date': f"{datetime.datetime.now()}",
         'pic': 'data:image/jpeg;base64,'},
        {'title': 'котовая', 'date': f"{datetime.datetime.now()}",
         'pic': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSpBw5aMBonj-sK9-cdjDUfgT-MYTWUfiKcNA&s'}
    ]
    return render_template('clothes.html', clothes=clothes)


@shop_app.route('/jackets/')
def jackets():
    jackets = [
        {'title': 'для пацанчика', 'date': f"{datetime.datetime.now()}",
         'pic': 'data:image/jpeg;base64,'},
        {'title': 'для пацанчика с Аляски', 'date': f"{datetime.datetime.now()}",
         'pic': 'data:image/jpeg;base64,'},
        {'title': 'для пацанки', 'date': f"{datetime.datetime.now()}",
         'pic': 'data:image/jpeg;base64,'}
    ]
    return render_template('jackets.html', jackets=jackets)


if __name__ == '__main__':
    shop_app.run(debug=True)
